import React from "react";

const AddPost = ({ isLoggedIn, currentUser }) => {




    return(
        <section className='add-post-container'>
            <h1>Add Post</h1>
            {isLoggedIn ?
                <form
                    id='add-post-form'
                    // onSubmit=
                >
                <h1>What would you like to post, {currentUser.username}?</h1>
                <label>Title</label>
                <input
                    type='text'
                    required
                    id='title-input'
                    placeholder="What're you looking to sell?">
                    {/* onChange=?? */}
                </input>

                </form>
            :
                <h1>Please log in to add a post!</h1>
            }
        </section>
    )
}




export default AddPost;
