import React, { useState, useEffect } from "react";
import RegisterForm from "./RegisterForm"
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

const LogIn = ({ currentUser, setCurrentUser }) => {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    useEffect(() => {
        const userFromLocalStorage = localStorage.getItem('currentUser');

        userFromLocalStorage ? 
            setCurrentUser(JSON.parse(userFromLocalStorage))
                    // console.log(userFromLocalStorage)
            :
            null
    },[])


    const loginUser = (evt) => {
        evt.preventDefault();
        const loggedInUserObj = {
            username,
            password
        }

        localStorage.setItem('currentUser', JSON.stringify(loggedInUserObj))
        localStorage.setItem('password', password)


        setCurrentUser(loggedInUserObj);
        setUsername('');
        setPassword('');
        console.log('this is our new local storage', localStorage)
    }
    const logOutUser = (evt) => {
        evt.preventDefault();
        console.log('loggin out', currentUser)
        localStorage.removeItem('currentUser')
        setCurrentUser({});
    }





    return (
        <section className='log-container'>
            <form 
                id='log-in-form'
                onSubmit={loginUser}
                >
                <h3>Log In</h3>
                <input
                     type='text'
                     required
                     value={username}
                     id="username"
                     placeholder="Username*"
                     onChange={(evt) => setUsername(evt.target.value)}
                ></input>
                <input
                    type='text'
                    required
                    value={password}
                    id='password'
                    placeholder='Password'
                    onChange={(evt) => setPassword(evt.target.value)}
                ></input>
                <input
                    type='submit'
                    value="Log In"
                ></input>
            </form>
            <div>
                {
                   currentUser.username && currentUser.password ? 
                   <div>
                    <p>You are logged in as {currentUser.username}</p>
                   <input
                    onClick={logOutUser}
                    type='submit'
                    value="Log Out"
                    ></input>
                    </div>
                   :
                   <div id='no-user-logged-in-container'>
                        <p>You are not logged in.</p>
                        <Link to="/register">Sign Up!</Link>
                   </div>
                }
            </div>
            <Routes>
                <Route path="/register" element={<RegisterForm />}/>
            </Routes>
        </section>

    )
}

export default LogIn;
