import React, { useEffect, useState } from "react";
import { BASE_URL, cohortName, fetchToken } from "../api";



const RegisterForm = ({ username, setUsername }) => {

    const [newPW1, setNewPW1] = useState('')
    const [newPW2, setNewPW2] = useState('')

    const createUser = (evt) => {
        evt.preventDefault();
        console.log('pw1', newPW1)
        console.log('pw2', newPW2)
    }




    return (
        <section className='register-form-container'>
            <form
                id='register-form'
                onSubmit={createUser}
                >
                <h3>Create a Log-In</h3>
                <input
                    type='text'
                    required
                    value={username}
                    id='username'
                    placeholder="Username"
                    onChange={(evt) => setUsername(evt.target.value)}
                ></input>
                <input
                    type='text'
                    required
                    value={newPW1}
                    id='password-submission1'
                    placeholder="Password"
                    onChange={(evt) => setNewPW1(evt.target.value)}
                ></input>
                <input
                    type='text'
                    required
                    value={newPW2}
                    id='password-submission2'
                    placeholder="Reconfirm Password"
                    onChange={(evt) => setNewPW2(evt.target.value)}
                ></input>
                <input
                    type='submit'
                    value='Create Account'
                ></input>
                <div>
                    {(newPW1 == newPW2 ) ? null : 'Passwords Must Match' }
                </div>
            </form>

        </section>
    )
}


export default RegisterForm;
