import React from "react";
import { useParams} from 'react-router-dom';



const EditPost = ({ isLoggedIn, username, token }) => {
    const { postId } = useParams();

    useEffect(() =>  {
        let returnedPosts= []

        const getPosts = async () => {
            returnedPosts = await fetchAllPosts(token);
            setPostList(returnedPosts);
        }
        getPosts()
    }, [isLoggedIn])

    return(
        <section id='edit-post-container'>
            <h1>Edit Post {postId} </h1>

                <form
                    id='edit-post-form'
                    // onSubmit={submitPost}
                >
                    <label className='form-label'>Title</label>
                    <input
                        type='text'
                        required
                        // value={title}
                        placeholder="title from og post"
                        // onChange={(evt) => setTitle(evt.target.value)}
                    >
                    </input>
                    <label className='form-label'>Description</label>
                    <input
                        type='text'
                        required
                        // value={description}
                        placeholder="desc from og post"
                        // onChange={(evt) => setDescription(evt.target.value)}
                    >
                    </input>
                    <label className='form-label'>Price</label>
                    <input
                        type='text'
                        required
                        // value={price}
                        placeholder="price from og post"
                        // onChange={(evt) => setPrice(evt.target.value)}
                    >
                    </input>
                    <label className='form-label'>Location</label>
                    <input
                        type='text'
                        // value={location}
                        placeholder="location from og post"
                        // onChange={(evt) => setLocation(evt.target.value)}
                    >
                    </input>
                    <label className='form-label'>Willing to Deliver?</label>
                    <input
                        type='checkbox'
                        // checked={willDeliver}
                        id="willdeliverycheckbox"
                        // onChange={(evt) => setWillDeliver(!willDeliver)}
                    >
                    </input>
                    <input
                        type="submit"
                        disabled= {isLoggedIn ? false : true}
                        value="Edit Post"
                    >
                    </input>
                </form>

        </section>
    )
}



export default EditPost;
