                    username,
                    password,
                    token
                }
                setCurrentUser(loggedInUserObj);
                localStorage.setItem('currentUser', JSON.stringify(loggedInUserObj))
                        localStorage.setItem('token', JSON.stringify(token))
                console.log('this is our currentUser', currentUser)

    return (
        <section className='log-container'>
            <form 
                id='log-in-form'
                onSubmit={loginUser}
                >
                <h3>Log In</h3>
                <input
                     type='text'
                     required
                     value={username}
                     id="username"
                     placeholder="Username*"
                     onChange={(evt) => setUsername(evt.target.value)}
                ></input>
                <input
                    type='text'
                    required
                    value={password}
                    id='password'
                    placeholder='Password*'
                    onChange={(evt) => setPassword(evt.target.value)}
                ></input>
                <input
                    type='submit'
                    value="Log In"
                ></input>
            </form>
            <div>
                {
                   token ? 
                   <div>
                    <p>You are logged in as {currentUser.username}</p>
                   <input
                    onClick={logOutUser}
                    type='submit'
                    value="Log Out"
                    ></input>
                    </div>
                   :
                   <div id='no-user-logged-in-container'>
                        <p>You are not logged in.</p>
                        <Link to="/register">Sign Up!</Link>
                   </div>
                }
            </div>
        </section>

    )
}

export default LogIn;
