import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { postMessage } from "../api";




const AddMessage = ({ username, token, isLoggedIn}) => {
    const { postId } = useParams();
    const [messageContent, setMessageContent] = useState('');
    const [messageSuccess, setMessageSuccess] = useState(false);
    const [newMessageDisplay, setNewMessageDisplay] = useState('')
    const [allPosts, setAllPosts] = useState([]);
    const [thisPost, setThisPost] = useState([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('')
    const [price, setPrice] = useState('')
    const [location, setLocation] = useState('[On Request')
    const [willDeliver, setWillDeliver] = useState(false)


    useEffect(() =>  {


        const getPosts = async () => {
            let returnedPosts = await fetchAllPosts(token);
            setAllPosts(returnedPosts);
        }
        getPosts()

        allPosts.map((eachPost) => {
            let postIdArray = postId.split('');
            postIdArray.shift()
            let colonlessPostId = postIdArray.join('')
                console.log("use effect postId: ", colonlessPostId);
            if (colonlessPostId === eachPost._id) {
                setTitle(eachPost.title)
                setDescription(eachPost.description)
                setPrice(eachPost.price)
                setLocation(eachPost.location)
                setWillDeliver(eachPost.willDeliver) 
            }
        })


    }, [])






    const submitMessage = async (evt) => {
        evt.preventDefault();
        let postIdArray = postId.split('');
        postIdArray.shift();
        let colonlessPostId = postIdArray.join('')
            console.log("token: ", token)
            console.log("postId: ", colonlessPostId)
            console.log("messagecontent: ", messageContent)
        const result = await postMessage(colonlessPostId, token, messageContent)
        if (result.success) {
            setMessageSuccess(true)}
            setNewMessageDisplay(result.data.message.content)
        return result;
    }


    return (
        <section id="add-message-container">

            <section className="each-post-section">
                <span className="post-label">Item: </span>
                <span className="post-title">{title}</span><br></br>
                <span className="post-label">Location: </span>
                <span className="post-location">{location}</span><br></br>
                <span className="post-label">Description: </span>
                <span className="post-description">{description}</span><br></br>
                <span className="post-label">Price: </span>
                <span className="post-price">{price}</span><br></br>
                <span className="post-label">Delivery Available: </span>
                <span className="post-delivery">{willDeliver ? "Yes" : "No"}</span><br></br>
            </section>

            <h1>Message Form Here, {username}</h1>
            <h4>Message for Post {postId}</h4>
            <form id= 'message-form'
                onSubmit={submitMessage}>
                <input
                    id='message-box'
                    type='text'
                    value={messageContent}
                    placeholder="message content"
                    onChange={(evt) => setMessageContent(evt.target.value)}
                    >
                </input>
                <input
                    type="submit"
                    disabled= {isLoggedIn ? false : true}
                    value="Submit">
                </input>
            </form>
            {
                messageSuccess ?
                <section id='new-message-container'>
                    <span className="message-label">Message: </span>
                    <span className="message-title">{newMessageDisplay}</span><br></br>                 
                </section>
                :
                null
            }

        </section>
    )

}

export default AddMessage;
