import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { postMessage } from "../api";




const AddMessage = ({ username, token, isLoggedIn}) => {
    const { postId } = useParams();
    const [messageContent, setMessageContent] = useState('');
    const [messageSuccess, setMessageSuccess] = useState(false);


    const submitMessage = async (evt) => {
        evt.preventDefault();
        let postIdArray = postId.split('');
        postIdArray.shift();
        let colonlessPostId = postIdArray.join('')
            console.log("token: ", token)
            console.log("postId: ", colonlessPostId)
            console.log("messagecontent: ", messageContent)
        const result = await postMessage(colonlessPostId, token, messageContent)
        if (result.success) {
            setMessageSuccess(true)}
        return result;
    }


    return (
        <section id="add-message-container">
            <h1>Message Form Here, {username}</h1>
            <h4>Message for Post {postId}</h4>
            <form id= 'message-form'
                onSubmit={submitMessage}>
                <input
                    id='message-box'
                    type='text'
                    value={messageContent}
                    placeholder="message content"
                    onChange={(evt) => setMessageContent(evt.target.value)}
                    >
                </input>
                <input
                    type="submit"
                    disabled= {isLoggedIn ? false : true}
                    value="Submit">
                </input>
            </form>
            {
                messageSuccess ?
                <section id='new-message-container'>
                    <span className="message-label">Message: </span>
                    <span className="message-title">{result.data.message.content}</span><br></br>                 
                </section>
                :
                null
            }

        </section>
    )

}

export default AddMessage;
