import React from "react";

const AddPost = ({ isLoggedIn, currentUser }) => {




    return(
        <section className='add-post-container'>
            <h1>Add Post</h1>
            {isLoggedIn ?

                <h1>What would you like to post, {currentUser.username}?</h1>

            :
                <h1>Please log in to add a post!</h1>
            }
        </section>
    )
}




export default AddPost;
